import React from "react";

class ReadString extends React.Component {
  state = { dataKeyBattery: null,dataKeyColumns: null };

  componentDidMount() {
    const { drizzle, drizzleState } = this.props;
    const contract = drizzle.contracts.MyStringStore;

    console.log(drizzle);
    console.log(drizzleState);

    // let drizzle know we want to watch the `myString` method
    const dataKeyBattery = contract.methods["batteries"].cacheCall();
    const dataKeyColumns = contract.methods["columns"].cacheCall();

    // save the `dataKey` to local component state for later reference
    this.setState({ dataKeyBattery, dataKeyColumns});
  }

  render() {
    // get the contract state from drizzleState
    const { MyStringStore } = this.props.drizzleState.contracts;
 
    // using the saved `dataKey`, get the variable we're interested in
    const batteries = MyStringStore.batteries[this.state.dataKeyBattery];
    const columns = MyStringStore.columns[this.state.dataKeyColumns];
    
    // if it exists, then we display its value
    return (
      			<div class="container-fluid">
              <div className="App">
                <div className="row">
                  <div className="col-md-12">
                    <div className="panel-group" id="accordion">
                      <div className="panel panel-default">
                        <div className="panel-heading">
                          <h4 className="panel-title">
                            <a
                              data-toggle="collapse"
                              data-parent="#accordion"
                              href="#collapseOne"
                            >
                              <div class="text-primary">NEW ORDER</div>
                            </a>
                          </h4>
                        </div>
    
                        <div
                          id="collapseOne"
                          className="panel-collapse collapse in"
                        >
                          <div className="panel-body">
                            <div className="row">
                              <div className="col-md-2"></div>
                              <div className="col-md-8">
                                <div className="form-group">
                                  <SetString
                                    drizzle={this.props.drizzle}
                                    drizzleState={this.state.drizzleState}
                                  />
                                </div>
                              </div>
                              <div className="col-md-2"></div>
                            </div>
                          </div>
                        </div>
                      </div>
                      <div className="panel panel-default">
                        <div className="panel-heading">
                          <h4 className="panel-title">
                            <a
                              data-toggle="collapse"
                              data-parent="#accordion"
                              href="#collapseTwo"
                            >
                              <div class="text-primary">DISPLAY ORDER</div>
                            </a>
                          </h4>
                        </div>
                        <div id="collapseTwo" className="panel-collapse collapse">
                          <div className="panel-body">
                            <div className="row">
                              <div className="col-md-3"></div>
                              <div className="col-md-6">
                                <div className="form-group">
                                  <ReadController
                                    drizzle={this.props.drizzle}
                                    drizzleState={this.state.drizzleState}
                                  />
                                </div>
                                <div className="form-group">
                                  <ReadCage
                                    drizzle={this.props.drizzle}
                                    drizzleState={this.state.drizzleState}
                                  />
                                </div>
                                <div className="form-group">
                                  <ReadMotor
                                    drizzle={this.props.drizzle}
                                    drizzleState={this.state.drizzleState}
                                  />
                                </div>{" "}
                                <div className="form-group">
                                  <ReadDoor
                                    drizzle={this.props.drizzle}
                                    drizzleState={this.state.drizzleState}
                                  />
                                </div>
                                <div className="form-group">
                                  <ReadButton
                                    drizzle={this.props.drizzle}
                                    drizzleState={this.state.drizzleState}
                                  />
                                </div>
                                <div className="form-group">
                                  <ReadDisplay
                                    drizzle={this.props.drizzle}
                                    drizzleState={this.state.drizzleState}
                                  />
                                </div>
                              </div>
                              <div className="col-md-3"></div>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
		);
  }
}

export default ReadString;
